CustomField.formula:
IF( T_C__c = "Non-Standard - Legal" || T_C__c = "Non-Standard - Commercial",
    IF(SBQQ__NetAmount__c < 3000000, 5,
        IF(AND(SBQQ__NetAmount__c >= 3000000, SBQQ__NetAmount__c < 6000000), 3,
            IF( SBQQ__NetAmount__c >= 6000000, 1, 9 
            )
        )
    ),
    IF(T_C__c = "MSSA",
        IF(SBQQ__NetAmount__c < 50000, 6,
            IF(AND(SBQQ__NetAmount__c >= 50000, SBQQ__NetAmount__c < 100000), 5,
                IF(AND(SBQQ__NetAmount__c >= 100000, SBQQ__NetAmount__c < 500000), 3, 
                    IF(AND(SBQQ__NetAmount__c >= 500000, SBQQ__NetAmount__c < 3000000), 2,
                       IF(SBQQ__NetAmount__c >= 3000000, 1, 9) 
                    ) 
                )
            )
        ),9    
    )
 )
